import styled from "styled-components";
import { HiOutlineShoppingCart } from "react-icons/hi";
import { FaCarSide } from "react-icons/fa";
import { RiMapPinLine } from "react-icons/ri";

export const Container = styled.div`
  width: 100%;
  height: 100px;
  max-height: 100px;
  overflow-y: hidden;

  background: var(--color-header);
  border-bottom: 1px solid var(--color-border);

  display: grid;
  grid-template-columns: 1fr 1200px 1fr;
  grid-template-areas: "NA MA NE";
  position: relative;
`;

export const Layout = styled.div`
  max-width: 1120px;
  margin: 0 auto;
  padding: 0 16px;
  grid-area: MA;
  display: grid;
  grid-template-rows: 50px 50px;
  grid-template-columns: 1fr 2fr 1fr;
  grid-template-areas:
    "ML IA FC"
    "CI CT AO";
`;

export const CepArea = styled.div`
  grid-area: ML CI;
  width: 100%;
  display: flex;
  flex-direction: column;
  justify-content: ;
  align-items: left;
  > img {
    width: 134px;
    margin-top: 10px;
    margin-bottom: 10px;
  }

  > h1 {
    font-size: 13px;
    font-weight: 400;
  }
  > h3 {
    font-size: 12px;
    font-weight: 400;
    padding: 6px 0px 0px 13px;
    opacity: 0.5;
  }
`;

export const MapPin = styled(RiMapPinLine)``;

export const InputArea = styled.div`
  grid-area: IA;
`;
export const Category = styled.div`
  grid-area: CT;
`;
export const AccountOptions = styled.div`
  grid-area: FC AO;
`;
export const CarIcon = styled(FaCarSide)``;

export const ShoppingIcon = styled(HiOutlineShoppingCart)``;
